using UnityEngine; public class Rotation : MonoBehaviour{ 	public float m_Speed = 100f;	public bool m_RotateX = false;	public bool m_RotateY = false;	public bool m_RotateZ = false;	public float m_RotateWorldSpeed = 32f;	public bool m_RotateWorldY = false;		void Update ()	{		if (m_RotateX)			transform.Rotate (m_Speed * Time.deltaTime, 0, 0);		if (m_RotateY)			transform.Rotate (0, m_Speed * Time.deltaTime, 0);		if (m_RotateZ)			transform.Rotate (0, 0, m_Speed * Time.deltaTime);					if (m_RotateWorldY)			transform.RotateAround (Vector3.zero, Vector3.up, Time.deltaTime * m_RotateWorldSpeed);	}}